var eejs = require('ep_etherpad-lite/node/eejs/');

exports.eejsBlock_scripts = function (hook_name, args, cb) {
	args.content = args.content + "<script src='https://autobahn.s3.amazonaws.com/autobahnjs/latest/autobahn.min.jgz'></script>";
	args.content = args.content + "<script src='../static/plugins/ep_add_buttons/static/js/socket.js'></script>";
	args.content = args.content + "<script src='../static/plugins/ep_add_buttons/static/js/analyze.js'></script>";
	args.content = args.content + "<script src='../static/plugins/ep_add_buttons/static/js/add_buttons.js'></script>";
	args.content = args.content + "<script src='../static/plugins/ep_add_buttons/static/js/add_dropdown.js'></script>";
	args.content = args.content + "<script src='../static/plugins/ep_add_buttons/static/js/accordeon.js'></script>";
	return cb();
}

exports.eejsBlock_styles = function (hook_name, args, cb) {
	args.content = args.content + "<link rel='stylesheet' href='../static/plugins/ep_add_buttons/static/css/font-awesome/css/font-awesome.min.css'>";
	args.content = args.content + "<link href='../static/plugins/ep_add_buttons/static/css/pad.css' rel='stylesheet'>";
	args.content = args.content + "<link rel='stylesheet' href='../static/plugins/ep_add_buttons/static/css/accordeon.css'>";
	return cb();
}

