/*
 * Check if the pad has to analyze again. Check for some predefined characters.
 */
exports.collectContentLineText = function(hook_name, args, cb) {
	
	//If one of this characters is at the end of the line
	var analyzeOn = ['.', '?', '!'];
	var lastChar = args.text.substr(args.text.length - 1);
	
	if ($.inArray(lastChar, analyzeOn) > -1) {
		analyzePadContent();
	}
	
	return args.text;
}