exports.eejsBlock_body = function (hook_name, args, cb) {
	//console.log("args inhoud:" + args);	
	
	var sideBar = "<div id='sidebar'><div id='sidebarInner'><button id='analyseButton'>Analyseer Tekst</button></div></div>";
	
	// add the sidebar to the body content, and also add the Analyse button
	var body = args.content;
	var position = body.search("editorcontainerbox") + 20;
	console.log("Position is:" + position);
	args.content = body.substr(0, position) + sideBar + body.substr(position);	
	
  	return cb();
}